        TireNode *p = root;
        for(int i = 0;i < key.size(); i++) {
            int index = key[i];
            if (p->child[index] == nullptr) {
                p->child[index] = new TireNode();
            }
            p = p->child[index];
        }
        p->value  = val;
        p->isLeaf = true;
    }

    int sum(string prefix) {
        TireNode *p = root;
        for(int i = 0;i < prefix.size() && p != nullptr; i++) {
            int index = prefix[i];
            p = p->child[index];
        }
        return p == nullptr ? 0 : sum(p);
