ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
    ListNode ret(0);
    ListNode *p = &ret;
    int add = 0,sum;
    while(l1 || l2 || add){
        sum = (l1?l1->val:0) + (l2?l2->val:0) + add;
        add = sum/10;
        p->next = new ListNode(sum%10);
        p = p->next;
        l1 = (l1?l1->next:nullptr);
        l2 = (l2?l2->next:nullptr);
    }
    return ret.next;
}
```

