vector<int> advantageCount1(vector<int>& A, vector<int>& B) {
    multiset<int> S(A.begin(), A.end());
    for(int i = 0;i < B.size(); i++) {
        auto it = S.upper_bound(B[i]);
        if (it == S.end()) {
            it = S.begin();
        }
        A[i] = *it;
        S.erase(it);
    }
    return A;
}
