class Solution {
public:
    int arrayNesting(vector<int>& nums) {

        int res = 0;
        for(int i = 0;i < nums.size(); i++) {
            if (nums[i] < 0) continue;
            res = max(res, helper(nums, i));
        }
        return res;
    }

    int helper(vector<int> &nums, int index) {
        int c = 0, j = nums[index];
        nums[index] = -1;
        while(j >= 0) {
            int t = nums[j];
            nums[j] = -1;
            j = t;
            c++;
        }
        return c;
    }
};
```

