        if (root) {
            inorderTraversal(root->left);
            vec.push_back(root->val);
            inorderTraversal(root->right);
        }
    }

    /** @return the next smallest number */
    int next() {
        return vec[index++];
    }

    /** @return whether we have a next smallest number */
    bool hasNext() {
        return index != vec.size();
    }
};
