class BSTIterator {
public:
    stack<TreeNode *> st;
    BSTIterator(TreeNode* root) {
        pushleft(root);
    }

    void pushleft(TreeNode *root) {
        while(root) {
            st.push(root);
            root = root->left;
        }
    }

    /** @return the next smallest number */
    int next() {
        auto root = st.top(); st.pop();
        pushleft(root->right);
        return root->val;
    }

    /** @return whether we have a next smallest number */
    bool hasNext() {
        return !st.empty();
    }
};
