class Solution {
public:
    int subarrayBitwiseORs(vector<int>& A) {
        int len = A.size();
        if (len == 0) return 0;

        unordered_set<int> res, cur, cur2;
        for(auto &i: A) {
            cur2 = {i};
            for(auto &j : cur) cur2.insert(i | j);
            cur = cur2;
            for(auto &j: cur) res.insert(j);
        }
        return res.size();
    }
};
```

