int countNumbersWithUniqueDigits(int n) {
    int *dp = new int[n+1];
    dp[0] = 1;
    int k = 9;
    int a = 9;
    for(int i = 1;i <= n && a > 0;i++) {
        dp[i] = k + dp[i-1];
        k *= a;
        a--;
    }
    int ret = dp[n];
    delete dp;
    return ret;
}
