        for(int j = 0;j < n; j++) {
            if (grid[i][j]) {
                a[i]++;
                b[j]++;
            }
        }
    }
    int res = 0;
    for(int i = 0;i < m; i++) {
        for(int j = 0;j < n; j++) {
            if (grid[i][j] && (a[i] > 1 || b[j] > 1))
                res++;
        }
    }
    return res;

}
