```c++
vector<int> dailyTemperatures(vector<int>& T) {
	int len = T.size();

	vector<int> res(len, 0);
	for(int i = len-2; i>= 0; --i) {
		int t = 1;
		while(T[i + t] <= T[i] && res[i+t] != 0)
			t += res[i+t];
		res[i] = (T[i+t] <= T[i])?0:t;
	}

	return res;
}
