```c++
int deepestLeavesSum(TreeNode* root) {
	if (root == nullptr) return 0;
	queue<TreeNode *> q;
	q.push(root);
	int sum = 0;
	while(!q.empty()) {
		sum = 0;
		for(int i = 0, size = q.size(); i < size; i++) {
			root = q.front(); q.pop();
			sum += root->val;
			if (root->left) q.push(root->left);
			if (root->right) q.push(root->right);
		}
	}
	return sum;
}
```

