    if (toDelNodes(root->right, delmap)) {
        root->right = nullptr;
    }

    if (delmap[root->val]) {
        if (root->left) res.push_back(root->left);
        if (root->right) res.push_back(root->right);
        return true;
    }
    return false;        
}
