bool isOp(const string &s) {
	return s.size() == 1 && (s[0] == '+' || s[0] == '-' || s[0] == '*' || s[0] == '/');
}
int calc(int a, char op, int b) {
	switch(op) {
		case '+': return a + b;
		case '-': return a - b;
		case '*': return a * b;
		case '/': return a / b;
	}
	return -1;
}
int evalRPN(vector<string>& tokens) {
	stack<int> st;
	for(auto &s: tokens) {
		if (!isOp(s)) {
			st.push(atoi(s.c_str()));
		} else {
			int b = st.top(); st.pop();
			int a = st.top(); st.pop(); 
			st.push(calc(a, s[0], b));
		}  // else return -1;
	}
	return st.top();
}
