            st.push(p);
            st.push(p->child);
        } else if (!st.empty()) {
            Node *parent = st.top(); st.pop();
            p->next = parent->next;
            if (p->next) p->next->prev = p;
            parent->next = parent->child;
            parent->next->prev = parent;
            parent->child = nullptr;
            st.push(p);
        }
    }
