            """
            self.child = {}
            self.count = 0

class Trie:

    def __init__(self):
        """
        Initialize your data structure here.
        """
        self.root = TrieNode()


    def insert(self, word):
        """
        Inserts a word into the trie.
        :type word: str
        :rtype: void
        """
        p = self.root
        for c in word:
            q = p.child.get(c,None)
            if q is None:
                p.child[c] = TrieNode()
                q = p.child[c]
            p = q
        p.count += 1

    def search(self, word):
        """
        Returns if the word is in the trie.
        :type word: str
        :rtype: bool
        """
        p = self.root
        for c in word:   
            q = p.child.get(c,None)
            if q is None:
                return False
            p = q
        return p.count > 0


    def startsWith(self, prefix):
        """
        Returns if there is any word in the trie that starts with the given prefix.
        :type prefix: str
        :rtype: bool
        """
        p = self.root
        for c in prefix:
            q = p.child.get(c,None)
            if q is None:
                return False
            p = q
        return True


