bool canJump1(vector<int> &nums,int last) {
    if (last == 0) return true;
    int k = 1;
    for(int i = last-1;i >= 0;i--,k++) {
        if (nums[i] >= k && canJump1(nums,i))
            return true;
    }
    return false;
}
