        int l = longestPalindrome(s,first,mid,lF);
        int r = longestPalindrome(s,mid + 1,last,rF);
        int ret = max(l,r);
        //
        int midMax = ret;
        for(int i = first;i <=mid ;i++)
            for(int j = mid + 1;j <= last;j++){
                if (j-i +1 > midMax && isPalindrome(s,i,j)){
                    midMax = j-i+1;
