```c++
int maxLevelSum(TreeNode* root) {
    if (!root) return -1;

    int max_level = -1, max_sum = INT_MIN;

    queue<TreeNode *> q;
    q.push(root);

    int cur_level = 1, cur_sum;

    while(!q.empty()) {
        cur_sum = 0;
        for(int i = 0, size = q.size(); i < size; i++) {
            root = q.front(); q.pop();
            cur_sum += root->val;
            if (root->left) q.push(root->left);
            if (root->right) q.push(root->right);
        }
        if (cur_sum > max_sum) {
            max_sum = cur_sum;
            max_level = cur_level;
        }
        cur_level++;
    }

    return max_level;
}
```

