int maxProduct(vector<int>& nums) {
    int i = 0,j = 0;
    int ret = INT_MIN;
    int now = 0;

    for(j = 0;j < nums.size();j++){
        if (nums[j] == 0){
            while(now < 0 && i < j -1)
                now /= nums[i++];
            ret = max(ret,now);
            if (ret < 0) ret = 0;
            now = 0;
            i = j+1;
        } else {
            now = (now)?now*nums[j]:nums[j];
            ret = max(ret,now);
        }
    }

    while(now < 0 && i < j - 1)
        now /= nums[i++];
    ret = max(ret,now);
    return ret;
}
