```c++
class Solution {
public:
    int minFallingPathSum(vector<vector<int>>& A) {
        int h = A.size(), w;
        if (h == 0) return 0;
        w = A[0].size();

        vector<int> dp0 = A[0];
        vector<int> dp1(w, INT_MAX);

        for(int i = 1; i < h; i++) {
            for(int j = 0;j < w; j++) {
                dp1[j] = INT_MAX;
                if (j > 0) dp1[j] = min(dp0[j-1], dp1[j]);
                dp1[j] = min(dp0[j], dp1[j]);
                if (j + 1 < w) dp1[j] = min(dp0[j+1], dp1[j]);
                dp1[j] += A[i][j];

            }
            swap(dp1, dp0);
        }

        int res = INT_MAX;
        for(auto &i: dp0) res = min(res, i);
        return res;
    }
};
```

