class Solution {
public:
    ListNode* oddEvenList(ListNode* head) {
        if (head == nullptr || head->next == nullptr) return head;

        ListNode even(0);
        ListNode *p = head;
        ListNode *q = &even;

        while(p->next && p->next->next) {
            q->next = p->next;
            p->next = p->next->next;
            p = p->next;
            q = q->next;
        }
        if (p->next != nullptr) {
            q->next = p->next;
            q = q->next;
        }
        p->next = even.next;
        q->next = nullptr;
        return head;
    }
};
