bool isPalindrome(ListNode* head) {
    if (!head || !head->next) return true;

    ListNode *fast = head;
    ListNode *slow = head;
    ListNode *pre = slow;
    while(fast && fast->next) {
        fast = fast->next->next;
        pre = slow;
        slow = slow->next;
    }
    fast = pre->next;
    pre->next = nullptr;

    fast = revertList(fast);

    while(fast && head) {
        if (fast->val != head->val)
            return false;
        fast = fast->next;
        head = head->next;
    }
    return true;
}
ListNode *revertList(ListNode *head) {
    if(!head || !head->next) return head;
    ListNode *pre = head;
    ListNode *cur = head;
    while(cur) {
        ListNode *t = cur->next;
        cur->next = pre;
        pre = cur;
        cur = t;
    }
    head = head->next;
    return pre;
}
