bool canPartitionKSubsets(vector<int>& nums, int k) {
    if ( k <= 1 ) return true;
    //return canPartitionKSubsets(nums.begin(),k/2);

    sort(nums.begin(),nums.end());

    int sum = 0;
    for(auto i:nums) 
        sum +=i;
    if (sum % k != 0) return false;
    int a = sum / k;
    vector<int> kdq(k,a);
    return possible(nums,kdq,nums.size() - 1);
