bool isSameTree(TreeNode* p, TreeNode* q) {
    if (p == nullptr && q == nullptr) return true;
    else if (p && q) {
        return q->val == p->val && isSameTree(p->left,q->left) && isSameTree(p->right,q->right);
    } else return false;
}
