    ListNode* sortList(ListNode* head) {
        if (head == nullptr || head->next == nullptr ) return head;

        ListNode *slow = head;
        ListNode *fast = head;
        ListNode *pre = head;

        while(fast && fast->next) {
            pre = slow;
            slow = slow->next;
            fast = fast->next->next;
        }

        pre->next = nullptr;
        head = sortList(head);
        slow = sortList(slow);

        return mergeList(head,slow);
    }
    ListNode *mergeList(ListNode *p1,ListNode *p2) {
        ListNode ret(0);
