```cpp
int myAtoi(string str) {
    int res = 0;

    // clear space
    int beg = 0;
    while(beg < str.size()) {
        char c = str[beg];
        if (c == ' ') beg++;
        else if (c == '-' || c == '+'||(c <= '9' && c >= '0')) break;
        else return res;
    }

    int sign = 1;
    int max_num = INT_MAX;
    // if we have '+' or '-'
    if (str[beg] == '+') {
        beg++;
    } else if (str[beg] == '-') {
        beg++;
        sign = -1; max_num = INT_MIN;
    }

    const int c = INT_MAX/10;

    for(;beg < str.size() && str[beg] >= '0' && str[beg] <= '9'; beg++) {
        int i = str[beg]-'0';
        if (res > c || (res == c && i > 7)) return max_num;
        res = res * 10 + i;
    }

    return sign*res;
}
```

