    ListNode *p = *head;
    ListNode *next = p->next;
    p->next = next->next;
    next->next = p;
    *head = next;
    return true;
}
ListNode* swapPairs(ListNode* head) {
    ListNode **p = &head;
    while(swap(p))
        p=&((*p)->next->next);
    return head;
}
