```c++
bool validateStackSequences(vector<int>& pushed, vector<int>& popped) {
    stack<int> st;
    int i = 0;
    for(auto &t: popped) {
        if (!st.empty() && st.top() == t) {
            st.pop();
        } else {
            while(i < pushed.size() && pushed[i] != t) {
                st.push(pushed[i]);
                i++;
            }
            if (pushed.size() == i) return false;
            i++;
        }
    }
    return true;
}
```

