        lefts.clear();
        rights.clear();

        helper(first, i, lefts);
        helper(i+1, last, rights);

        for(auto l: lefts) {
            for(auto r: rights) {
                outputs.push_back(calc(ops[i], l, r));
            }
        }
    }

}
