vector<int> searchRange1(vector<int>& nums, int target) {
    int first = 0,last = nums.size() - 1,mid;
    vector<int> ret{-1,-1};
    while(first <= last) {
        mid = (first + last)/2;
        if (nums[mid] == target){
            break;
        } 
        else if (nums[mid] < target) first = mid+1;
        else last = mid - 1;
    }

    int l = mid,f = mid;
    ret[0] = first;
    ret[1] = last;

    while(first <= l) {
        int m = (first + l)/2;
        if (nums[m] == nums[mid])
            l = m - 1;
        else if (nums[m+1] != nums[mid]) first = m + 1;
        else {
            ret[0] = m+1;
            break;
        }
    }
    while(f <= last) {
        int m = (f + last)/2;
        if (nums[m] == nums[mid]) f = m + 1;
        else if (nums[m-1] != nums[mid]) last = m - 1;
        else {
            ret[1] = m - 1;
            break;
        }
    }

    return ret;
}
