    while( (mid = searchRangeIter(nums,mid+1,nums.size() - 1,target)) != -1)
        ret[1] = mid;
    return ret;
}
int searchRangeIter(vector<int> nums,int first,int last,int target) {
    while(first <= last) {
        int mid = (first + last)/2;
        if (nums[mid] == target){
            return mid;  
        } 
        else if (nums[mid] < target) first = mid+1;
        else last = mid - 1;
    }
    return -1;
}
